#!/usr/bin/env bash

#line="nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
#line="bvwbjplbgvbhsrlpgdmjqwftvncz"
#line="zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
#line="nppdvjthqldpwncqszvftbrmjlhg"
#line="abcdeh"
line=""

#match_len=4
match_len=14

echo "len ${#line}"
end=$(( ${#line} + 1 - $match_len ))
for (( idx=0; idx<$end; idx++ )) ; do
    sub="${line:$idx:$match_len}"
    found=1
    for (( jj=0; jj<$match_len; jj++ )) ; do
        ch="${sub:$jj:1}"
        ss1="${sub%$ch*}"
        if (( ${#ss1} > $jj )) ; then
            found=0
            break
        fi
    done
    if (( $found == 1 )) ; then
        echo "idx: $(( $idx + $match_len ))"
        break
    fi
done